query:
select employee as 'كود الموظف::120', employee_name as 'إسم الموظف::250', f_si_e as 'أساسى حصة العامل:Currency:120', 
v_si_e as 'متغيير حصة العامل:Currency:120', f_si_c as 'أساسى حصة الشركة:Currency:120', 
v_si_c as 'متغيير حصة الشركة:Currency:120', f_si_ec as 'أساسى حصة العامل محملة على الشركة:Currency:120', 
v_si_ec as 'متغيير حصة العامل محملة على الشركة:Currency:120', (f_si_e+v_si_e+f_si_c+v_si_c+f_si_ec+v_si_ec) as 'المجموع:Currency:120' 
from (
select employee , employee_name , round(sum(f_si_e),2) as f_si_e, 
round(sum(v_si_e),2) as v_si_e, round(sum(f_si_c),2) as f_si_c, 
round(sum(v_si_c),2) as v_si_c, round(sum(f_si_ec),2) as f_si_ec, 
round(sum(v_si_ec),2) as v_si_ec 
from ( select ss.employee, ss.employee_name, sd1.parent, sd1.idx, 
if(sd1.parentfield='deductions' and sd1.salary_component='تأمينات حصة العامل أساسى',sd1.amount,0) as f_si_e, 
if(sd1.parentfield='deductions' and sd1.salary_component='تأمينات حصة العامل متغيير',sd1.amount,0) as v_si_e, 
if(sd1.parentfield='deductions' and sd1.salary_component='تامينات حصة صاحب العمل - اساسي',sd1.amount,0) as f_si_c, 
if(sd1.parentfield='deductions' and sd1.salary_component='تامينات حصة صاحب العمل - متغير',sd1.amount,0) as v_si_c, 
if(sd1.parentfield='earnings' and sd1.salary_component='تأمينات حصة العامل أساسى',sd1.amount,0) as f_si_ec, 
if(sd1.parentfield='earnings' and sd1.salary_component='تأمينات حصة العامل متغيير',sd1.amount,0) as v_si_ec 
from `tabSalary Detail` sd1 
left join `tabSalary Slip` ss on ss.name=sd1.parent
where ss.docstatus=%(docst)s and month(ss.end_date)=%(mm)s and ss.salary_slip_type=%(sst)s and ss.company=%(company)s and ss.business_unit=%(business_unit)s) a
group by employee) b
